import {to_root_path} from '../../paths.js';

export const render_ts_header_and_footer = (
	{origin_id}: {origin_id: string},
	content: string,
): string => {
	const origin_root_path = to_root_path(origin_id);
	return `// generated by ${origin_root_path}

${content}

// generated by ${origin_root_path}`;
};
