import {toRootPath} from '../../path/paths.js';

export const renderTsHeaderAndFooter = (
	{originId}: {originId: string},
	content: string,
): string => {
	const originRootPath = toRootPath(originId);
	return `
		// generated by ${originRootPath}

		${content}

		// generated by ${originRootPath}
	`;
};
